#include "stdlib.h"
#include "stdio.h"

char *get_test_string(unsigned long len)
{
	long unsigned l = len;
	char *str = NULL;

	// memory allocation
	do
	{
		len = l;
		str = calloc(len, sizeof(char));
		l >>= 1;
	}
	while (str == NULL);

	// this ensures that the same seed is always used
	// this is done in order to test reproductively
	srand(0);

	// string generation
	unsigned long i;
	for (i = 0; i < len; ++i)
	{
		if ((i + 1) % 10000000 == 0)
			printf("Generating string: %lu / %lu\n", i + 1, len);

		str[i] = (rand() % 127) + 1;
	}
	str[i] = 0;

	return str;
}

